/**
 * HTTP status code for OK (200).
 * @type {number}
 */
export const OK: number;

/**
 * HTTP status code for Bad Request (400).
 * @type {number}
 */
export const BAD_REQUEST: number;

/**
 * HTTP status code for Not Found (404).
 * @type {number}
 */
export const NOT_FOUND: number;

/**
 * HTTP status code for Internal Server Error (500).
 * @type {number}
 */
export const INTERNAL_SERVER: number;

/**
 * HTTP status code for Not Modified (304).
 * @type {number}
 */
export const NOT_CHANGED: number;

/**
 * HTTP status code for Forbidden (403).
 * @type {number}
 */
export const FORBIDDEN: number;

/**
 * HTTP status code for Not Authorized (401).
 * @type {number}
 */
export const NOT_AUTHORIZED: number;

/**
 * HTTP status code for Conflict (409).
 * @type {number}
 */
export const ALREADY_EXISTS: number;
